package week1.day2;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Print even Numbers
		
		int value =20;
		
		for(int i=1; i<value; i++)
		{
			if(i%2 == 1)
			{
				System.out.println("Even Number = " + i);
			}
		}
	}

}
